    private void BntAddDrinkActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BntAddDrinkActionPerformed
        Cart.ListOfProduct.add("Drink,"+labelDrinkName.getText() + "," + spQuantity.getValue().toString()+"," + lableTotal.getText());
        if (!spCups.getValue().toString().equals("0")){
            Cart.ListOfProduct.add("Cups," + spCups.getValue().toString());
        }
        dispose();
    }//GEN-LAST:event_BntAddDrinkActionPerformed

