    private void spQuantityStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_spQuantityStateChanged
        try {
            Integer price = 0;
            switch (labelDrinkName.getText()) {
                case "Orange Juice":
                    price = GetData.getDrinkPrice("orange_juice");
                    break;
                case "Mango Juice":
                    price = GetData.getDrinkPrice("mango_juice");
                    break;
                default:
                    price = GetData.getDrinkPrice(labelDrinkName.getText());
                    break;
            }
            Integer quatity = Integer.parseInt(spQuantity.getValue().toString());
            AddDrink.lableTotal.setText( price * quatity + " $");
        } catch (SQLException ex) {
            Logger.getLogger(AddDrink.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_spQuantityStateChanged

